
public class Displaying {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Screen  Display");
		for( int  i=1;i<=9;i++) {
			for( int j=1;j<=i;j++) {
				System.out.print(" " );
				System.out.print("*");
			}
			System.out.print("\n");
			
		}

	}

}
